  }

  return (
    <div className="bodyStyle">
      <h1 className="wText">Rock Paper Scissors</h1>
      <article>
        <div>
          <h2 className="wText">Player</h2>
          <h2 className="wText">Score</h2>
          <h2 className="wText">Computer</h2>
        </div>
        <span>player {playerState}</span>
        <span>Computer {computerState}</span>
      </article>
