import React from 'react';
import reactDom from 'react-dom';

class Homepage extends React.Component() {
  constructor(props) {
    super(props);
    this.state = {
      value: null,
    };
  }
  bodyStyle = {
    backgroundColor: 'mediumpurple'
  }
  wText = {
    color: 'antiquewhite'
  }


  render () {
    <div style={bodyStyle}>
      <h1 style={wText}>Rock Paper Scissors</h1>
      <article>
        <div>
          <h2 style={wText}>Player</h2>
          <h2 style={wText}>Score</h2>
          <h2 style={wText}>Computer</h2>
        </div>
        <span>{this.state.value}</span>
      </article>
      <main>
        <div>
          <button onClick={() => this.setState({value: 'rock'}) }>rock</button>
          <button onClick={() => this.setState({value: 'paper'}) }>paper</button>
          <button onClick={() => this.setState({value: 'scissors'})  }>scissors</button>
        </div>
      </main>
      <footer>
        <div>
          <h2>HighScore</h2>
        </div>
      </footer>
    </div>
  }
}
export default Homepage;
